            })
        })
        .on('confirmation', function(confNumber, receipt, latestBlockHash){ 
            window.alert("Congratulations! Your diamond has been mined!")
        })
        .on('error', function(error){ 
            window.alert("Mint failed!\n\nPlease try again.")
        })
    }

