    generateSparkle() {
        var num = Math.floor(Math.random() * 2);

        // Case 0: solid, Case 1: solid + flicker, Case 2: none
        switch (num) {
            case 0:
                var sparkle = '1xxxxxxx ';
                break
            case 1:
                sparkle = '1flicker ';
                break
            case 2:
                sparkle = '0xxxxxxx ';
                break
            default:
                sparkle = '0xxxxxxx ';
                break
        }

        return sparkle;
    }

