import React, { Component } from 'react';
import Web3 from 'web3';
import Diamonds from '../abis/Diamonds.json';
import {MDBCard, MDBCardBody, MDBCardTitle, MDBCardText, MDBCardImage, MDBBtn} from 'mdb-react-ui-kit';
import './App.css';
// import detectEthereumProvider from "@metamask/detect-provider";

class App extends Component {

    // async componentDidMount() {
    //     await this.loadWeb3();
    //     await this.loadBlockchainData();
    // }

    // // first up is to detect ethereum provider
    // async loadWeb3() {
    //     const provider = await detectEthereumProvider();

    //     // modern browsers
    //     // if there is a provider then lets
    //     // lets log that it's working and access the window from the doc
    //     // to set Web3 to the provider 

    //     if(provider) {
    //         console.log('ethereum wallet is connected')
    //         window.web3 = new Web3(provider)
    //     } else {
    //         // no ethereum provider
    //         console.log('no ethereum wallet detected')
    //     }
    // }

    // async loadBlockchainData() {
    //     const web3 = window.web3
    //     const accounts = await web3.eth.getAccounts()
    //     this.setState({account:accounts[0]})

    //     // create a constant js variable networkId which 
    //     //is set to blockchain network id 
    //     const networkId = await web3.eth.net.getId()
    //     const networkData = Diamonds.networks[networkId]
    //      if(networkData) {
    //          // EXERCISE TIME!!!! :)
    //          // 1. create a var abi set to the Kryptobird abi
    //          // 2. create a var address set to networkData address
    //          // 3. create a var contract which grabs a 
    //          //new instance of web3 eth Contract  
    //          // 4. log in the console the var contract successfully - GOOD LUCK!!!!

    //          const abi = Diamonds.abi;
    //          const address = networkData.address; 
    //          const contract = new web3.eth.Contract(abi, address)
    //          this.setState({contract})

    //          // call the total supply of our Krypto Birdz 
    //          // grab the total supply on the front end and log the results
    //          // go to web3 doc and read up on methods and call 
    //          const totalSupply = await contract.methods.totalSupply().call()
    //         this.setState({totalSupply})
    //         // set up an array to keep track of tokens 
    //         // load KryptoBirdz
    //         for(let i = 1; i <= totalSupply; i++) {
    //             const diamond = await contract.methods.diamonds(i - 1).call()
    //             // how should we handle the state on the front end? 
    //             this.setState({
    //                 diamonds:[...this.state.diamonds, diamond]
    //             })
    //         }
    //      } else {
    //          window.alert('Smart contract not deployed')
    //      }
    // }

    async componentWillMount() {
        await this.loadWeb3()
        await this.loadBlockchainData()
    }

    async loadWeb3() {
        if (window.ethereum) {
          window.web3 = new Web3(window.ethereum)
          await window.ethereum.enable()
        }
        else if (window.web3) {
          window.web3 = new Web3(window.web3.currentProvider)
        }
        else {
          window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
        }
      }

    async loadBlockchainData() {
        const web3 = window.web3
        const accounts = await web3.eth.getAccounts()
        // const accounts = await ethereum.request({ method: 'eth_accounts' });
        this.setState({account:accounts[0]})

        //get contract
        const networkId = await web3.eth.net.getId();
        const networkData = Diamonds.networks[networkId];

        if(networkData) {
            const abi = Diamonds.abi;
            const address = networkData.address;
            const contract = new web3.eth.Contract(abi, address);
            this.setState({contract})

            //get total supply
            const totalSupply = await contract.methods.totalSupply().call()
            this.setState({totalSupply})

            //load diamonds
            for(let i = 0; i < totalSupply; i++) {
                const diamond = await contract.methods.diamonds(i).call()
                this.setState({
                    diamonds:[...this.state.diamonds, diamond]
                })
            }

        } else {
            window.alert("Smart contract not deployed.")
        }
    }

    mint = (diamond) => {
        this.state.contract.methods.mint(diamond).send({from:this.state.account})
        .once('receipt', (receipt) => {
            this.setState({
                diamonds:[...this.state.diamonds, diamond]
            })
        })
        .on('confirmation', function(confNumber, receipt, latestBlockHash){ 
            window.alert("Congratulations! Your NFT has been minted!\n\n" + diamond)
        })
        .on('error', function(error){ 
            window.alert("Mint failed!\n\nPlease try again.")
        })
    }



    constructor(props) {
        super(props);
        this.state = {
            account: '',
            contract: null,
            totalSupply: 0,
            diamonds:[],
            testimages: ['https://i.ibb.co/3TBLH2B/d1.png', 'https://i.ibb.co/jfH8wzv/d2.png'],
            testcolor: ['#753188','#E59934']
        }
    }

    render() {
        return (
            <div className='container-full'>
                {console.log(this.state.diamonds)}
                <nav className='navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow'>
                    <div className='navbar-brand col-sm-3 col-md-3 mr-0' style={{color:'white'}}>
                        Diamond Hands
                    </div>
                    <ul className='nav-bar-nav px-3'>
                        <li className='nav-item text-nowrap d-none d-sm-none d-sm-block'>
                            <small className='text-white'>
                                {this.state.account}
                            </small>
                        </li>
                    </ul>
                </nav>

                <div className='container-fluid mt-1'>
                    <div className='row'>
                        <main role='main' className='col-lg-12 d-flex text-center'>
