import React, { Component } from 'react';
import Web3 from 'web3';
import Diamonds from '../abis/Diamonds.json';

class App extends Component {

    async componentWillMount() {
        await this.loadWeb3()
        await this.loadBlockchainData()
    }

    async loadWeb3() {
        if (window.ethereum) {
          window.web3 = new Web3(window.ethereum)
          await window.ethereum.enable()
        }
        else if (window.web3) {
          window.web3 = new Web3(window.web3.currentProvider)
        }
        else {
          window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
        }
      }

    async loadBlockchainData() {
        const web3 = window.web3
        const accounts = await web3.eth.getAccounts()
        // const accounts = await ethereum.request({ method: 'eth_accounts' });
        this.setState({account:accounts})

        //get contract
        const networkId = await web3.eth.net.getId();
        const networkData = Diamonds.networks[networkId];

        if(networkData) {
            const abi = Diamonds.abi;
            const address = networkData.address;
            const contract = new web3.eth.Contract(abi, address);
            this.setState({contract})

            //get total supply
            const totalSupply = await contract.methods.totalSupply().call()
            this.setState({totalSupply})

            //load diamonds
            for(let i = 0; i < totalSupply; i++) {
                const diamond = await contract.methods.diamonds(i).call()
                this.setState({
                    diamonds:[...this.state.diamonds, diamond]
                })
            }

        } else {
            window.alert("Smart contract not deployed.")
        }
    }

    constructor(props) {
        super(props);
        this.state = {
            account: '',
            contract: null,
            totalSupply: 0,
            diamonds:[]
        }
    }

    render() {
        return (
            <div>
                <nav className='navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow'>
                    <div className='navbar-brand col-sm-3 col-md-3 mr-0' style={{color:'white'}}>
                        Diamond Hands
                    </div>
                    <ul className='nav-bar-nav px-3'>
                        <li className='nav-item text-nowrap d-none d-sm-none d-sm-block'>
                            <small className='text-white'>
                                {this.state.account}
                            </small>
                        </li>
                    </ul>
                </nav>
            </div>
        )
    }

}

export default App;
